import React, { Component } from "react";
import Loginaxios from "./loginaxios";
import { Navbar } from "react-bootstrap";
// import "../subcomponent/login.css"
class Registercomponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      UserId: 0,
      RegUserName: "",
      RegPassword: "",
      message: "",
      departments: [],
      isRegUserNameValid:true,
      isRegPasswordValid:true,
      isFormValid:true
    };
    this.serv = new Loginaxios();
  }
  register() {
    let user = {
      UserId: this.state.UserId,
      UserName: this.state.RegUserName,
      PAssword: this.state.RegPassword,
};
    this.serv
      .registerUser(user)
      .then((resp) => {
        this.setState({ message: resp.data.message });
      })
      .catch((error) => {
        this.setState({ message: `Error Occured ${error.message}` });
      });
  }

  handleInPutChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
    this.validateInputs(evt.target.name,evt.target.value);
  };

  // validation logic
validateInputs=(name,value)=>{
    if(name === 'RegUserName'){
        if(value.length === 0) {
            this.setState({isRegUserNameValid:false});
            this.setState({isFormValid:false});
            alert('Enter valid email')
        }else {
            this.setState({isRegUserNameValid:true});
            this.setState({isFormValid:true});
        }
    }
    if(name === 'RegPassword'){
        if(value.length === 0) {
            this.setState({isRegPasswordValid:false});
            this.setState({isFormValid:false});
        }else {
            this.setState({isRegPasswordValid:true});
            this.setState({isFormValid:true});
        }
    }

  }

  render() {
    return (

      <>
       <div align='right'>
       <Navbar style={{background:'black',width:1089 ,height:62}}>
       </Navbar>
       </div>
       <div class="card " style={{width:1089} } >
        <form style={{width:1089}}>
        
        <h3 align='center'>Register New User </h3> 
               <div className='form-group row'>
               <label style={{fontFamily:"-moz-initial" ,fontSize:20}} class="col-sm-2 col-form-label"> User Id</label>
               <input 
               type='text'class="col-sm-9" 
               placeholder='Patient Id'  
               name='Patient_Id'
               defaultValue={this.state.Patient_Id}
               onChange={this.handleAllChanges.bind(this)}
               
               />
               </div><br/>
               </form>
               </div>

        <h3>Register User </h3> 
          <form className='calculator card' style={{width:500} }>
          UserId
          <input type="text" name="UserId" onChange={this.handleInPutChanges.bind(this)} className="form-control"/>
          User Name
          <input type="text" name="RegUserName" value={this.state.RegUserName} onChange={this.handleInPutChanges.bind(this)} className="form-control" />
          Password
          <input type="password" className="form-control" name="RegPassword" value={this.state.RegPassword} onChange={this.handleInPutChanges.bind(this)} />
          <div className="alert alert-danger" hidden={this.state.isRegPasswordValid}> The Password is Required </div>
          <input type="button" value="Register" onClick={this.register.bind(this)} disabled={!this.state.isFormValid} className="btn btn-light"/>
           </form >
          
                   
</>
    );
  }
}

export default Registercomponent;
