import React ,{ Component } from "react";
import {Route,Redirect,Link,Switch} from "react-router-dom";
import { SecureCallService } from "./secureservice";


class Homepage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: "",
            departments: [],
            columnHeaders:[] 
          }  
          this.serv = new SecureCallService();
    }
    handledelete(c){
        let ptid = c.target.value
        this.serv.delete(ptid)
            .then((resp) => {
                this.setState({ message: resp.data.message });
                window. location. reload();
            })
            .catch((error) => {
                this.setState({ message: `Error Occured ${error.message}` });
            });
    }

    getValues() {
        let token = sessionStorage.getItem("token");
        if (token === undefined) {
          this.setState({ message: `Please send the Auth Header` });
        } else {
          this.serv.getData(token)
            .then((resp) => {
              this.setState({ departments: resp.data.message });
              this.setState({ message: `Data Received Successfully` });
              this.setState({columnHeaders:Object.keys(this.state.departments[0])})
            })
            .catch((error) => {
              this.setState({ message: `Error Occured ${error.message}` });
            });
        }
      }
    render() { 
        return ( 
          <>
            <div>
            <input type="button" value="Get Data" onClick={this.getValues.bind(this)} className="btn btn-danger" /> 
            <br/> <br/>
            <strong> {JSON.stringify(this.state.departments)}</strong>
            <div>
            
            <table className="table table-bordered table-secondary">
          <thead>
            <tr>
              {this.state.columnHeaders.map((head, idx) => (
                <th key={idx}>{head}</th>
                
              ))}
             
              <td>
                <th>Edit</th>
              </td>
              <td>
                <th>Delete</th>
              </td>
              
            </tr>
          </thead>

          <tbody>
            {this.state.departments.map((doc, idx) => (
              <tr key={idx}>
                {this.state.columnHeaders.map((head, i) => (
                  <td key={i}>{doc[head]}</td>
                ))}
                
                <td>
                    <button >
                    <Link to={`/updatedoctor/${doc.ptid}`}>Update</Link>
                    </button>
                </td>
                <td>
                {/* <button >
                    <Link to={`/delete/${doc.ptid}`}>Delete</Link>
                    </button> */}
                    <button onClick={this.handledelete} value={doc.ptid}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
            </div>
            
            </div>
            <div className= "sidenav">
            <ul className="pagination" style={{display:"inline-block"}}>
            <li class="page-item " ><a class="page-link" style={{width:500}} href="#section">Patient</a><br/></li>
            <li class="page-item " > <a class="page-link" href="#section">Doctor</a><br/></li>
            <li class="page-item " ><a class="page-link" href="#section">Wards</a><br/></li>
            <li class="page-item " ><a class="page-link" href="#section">Wardboy</a><br/></li>
            </ul>
        </div>
            </>
         );
    }
}
 
export default Homepage;
