import React, { Component } from "react";
import Loginaxios from "./loginaxios";
import image from "./image/hospital.png"
import logo from "./image/new.png"
import dd from "./image/hhh.png"
// import "../subcomponent/login.css"
class HttpCallComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      UserId: 0,
      RegUserName: "",
      RegPassword: "",
      AuthUserName: "",
      AuthPassword: "",
      message: "",
      departments: [],
      isRegUserNameValid:true,
      isRegPasswordValid:true,
      isFormValid:true
    };
    this.serv = new Loginaxios();
  }

  clearReg = () => {
    this.setState({ UserId: 0 });
    this.setState({ RegUserName: "" });
    this.setState({ RegPassword: "" });
  };
 
  register() {
    let user = {
      UserId: this.state.UserId,
      UserName: this.state.RegUserName,
      PAssword: this.state.RegPassword,
};
    this.serv
      .registerUser(user)
      .then((resp) => {
        this.setState({ message: resp.data.message });
      })
      .catch((error) => {
        this.setState({ message: `Error Occured ${error.message}` });
      });
  }

  auth() {
    let user = {
      UserName: this.state.AuthUserName,
      Password: this.state.AuthPassword,
    };
    this.serv
      .authUser(user)
      .then((resp) => {
        console.log(resp.data)
        this.setState({ message: resp.data.message });
        sessionStorage.setItem("token", resp.data.token);
        sessionStorage.setItem("Designation",resp.data.designation)
        alert('Login sucessfully')
        this.props.history.push('/homepage');
      })
      .catch((error) => {
        this.setState({ message: `Error Occured Enter a valid Id and  password` });
      });
  }
  handleInPutChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
    this.validateInputs(evt.target.name,evt.target.value);
  };

  // validation logic
validateInputs=(name,value)=>{
    if(name === 'RegUserName'){
        if(value.length === 0) {
            this.setState({isRegUserNameValid:false});
            this.setState({isFormValid:false});
            alert('Enter valid email')
        }else {
            this.setState({isRegUserNameValid:true});
            this.setState({isFormValid:true});
        }
    }
    if(name === 'RegPassword'){
        if(value.length === 0) {
            this.setState({isRegPasswordValid:false});
            this.setState({isFormValid:false});
        }else {
            this.setState({isRegPasswordValid:true});
            this.setState({isFormValid:true});
        }
    }

  }

  render() {
    return (
      <div className="container-lg">

<div class="container-fluid px-1 px-md-5 px-lg-1 px-xl-5 py-5 mx-auto">
    <div class="card card0 border-0">
        <div class="row d-flex">
            <div class="col-lg-6">
                <div class="card1 pb-5"> 
                    <div class="row" > <img src={logo} style={{
                      width: '200px',
                      height: '100px',
                      margintop: '20px',
                      marginleft: '35px'
                    }}class="logo"/> </div> 

                    <div class="row px-3 justify-content-center mt-4 mb-5 border-line"> <img src={dd} style={
                      {
                          width: '450px',
                          height: '500px'

                    }}class="image"/> </div>
                </div>
            </div>
            <div class="col-lg-6">
                <div class="card2 card border-0 px-4 py-5">
                    <div class="row mb-4 px-3">
                        <h6 class="mb-0 mr-4 mt-2">Sign in with</h6>
                        <div class="facebook text-center mr-3">
                            <div class="fa fa-facebook"></div>
                        </div>
                        <div class="twitter text-center mr-3">
                            <div class="fa fa-twitter"></div>
                        </div>
                        <div class="linkedin text-center mr-3">
                            <div class="fa fa-linkedin"></div>
                        </div>
                    </div>
                    <div class="row px-3 mb-4">
                        <div class="line"></div> <small class="or text-center">Or</small>
                        <div class="line"></div>
                    </div>
                    <div class="row px-3"> <label class="mb-1">
                            <h6 class="mb-0 text-sm">Email Address</h6>
                        </label> <input class="mb-4" type="text" name="email" placeholder="Enter a valid email address"/> </div>
                    <div class="row px-3"> <label class="mb-1">
                            <h6 class="mb-0 text-sm">Password</h6>
                        </label> <input type="password" name="password" placeholder="Enter password"/> </div>
                    <div class="row px-3 mb-4">
                        <div class="custom-control custom-checkbox custom-control-inline"> <input id="chk1" type="checkbox" name="chk" class="custom-control-input"/> <label for="chk1" class="custom-control-label text-sm">Remember me</label> </div> <a href="#" class="ml-auto mb-0 text-sm">Forgot Password?</a>
                    </div>
                    <div class="row mb-3 px-3"> <button type="submit" class="btn btn-blue text-center">Login</button> </div>
                    <div class="row mb-4 px-3"> <small class="font-weight-bold">Don't have an account? <a class="text-danger ">Register</a></small> </div>
                </div>
            </div>
        </div>
        <div class="bg-blue py-4">
            <div class="row px-3"> <small class="ml-4 ml-sm-5 mb-2">Copyright &copy; 2019. All rights reserved.</small>
                <div class="social-contact ml-4 ml-sm-auto"> <span class="fa fa-facebook mr-4 text-sm"></span> <span class="fa fa-google-plus mr-4 text-sm"></span> <span class="fa fa-linkedin mr-4 text-sm"></span> <span class="fa fa-twitter mr-4 mr-sm-5 text-sm"></span> </div>
            </div>
        </div>
    </div>
</div>



















{/*       
       <div
        class="bg_image"
        style={{
          backgroundImage: 'url('+image+')',
          backgroundSize: "cover",
          height: "100vh",
          color: "#f5f5f5"
        }}

      >
        <h1 align='center' style={{color:'black',fontFamily:"cursive"}}>Hospital Managment System </h1>
      <br/><br/><br/>
       <center>
            <form className='calculator' style={{width:700}}>
          
           <h1 style={{fontFamily:"-moz-initial" ,fontSize:40 ,color:"black"}}>Login</h1>
          
            <div className='form-group row'>
               <label style={{fontFamily:"-moz-initial" ,fontSize:25 ,color:"black" }} class="col-sm-3 col-form-label"> User Name</label>
               <input 
               type='text'class="col-sm-9" 
               placeholder='User Name'  
               name='AuthUserName'
               Value={this.state.AuthUserName}
               onChange={this.handleInPutChanges.bind(this)}
               />
               </div><br/><br/>
