import React, { Component } from "react";
import Patientaxios from "../patient/patientaxios";
import { Navbar } from "react-bootstrap";
class Adddoctor extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Doctor_Id:0,
      Doctor_Name: "",
      Doctor_Address: "",
      Doctor_City: "",
      Doctor_Age:0,
      Doctor_Gender: "",
      Doctor_sspecalization: "",
      Doctor_Mobile_No: "",
      Doctor_fess_per_vist:"",
      departments: [],
      columnHeaders: [],
      message: "",
    }
    this.serv = new Patientaxios();
  }


  handleAllChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
    // console.log(evt.target.name , evt.target.value);
  };
  save = () => {
  var Patient = {
    ptid:this.state.Doctor_Id,
    Ptname: this.state.Doctor_Name,
    Paddress: this.state.Doctor_Address,
    ptcity: this.state. Doctor_City,
    ptage: this.state. Doctor_Age,
    ptgender: this.state.Patient_Gender,
    ptdob: this.state.Date_of_Birth,
    ptdisease: this.state.Patient_Disease,
    ptmobileno: this.state.Patient_Mobile_No,
    pttype :this.state.Patient_Type,
    wardtypeid: this.state.Patient_Ward_ID
  };
  this.serv.postpatientinfo(Patient)
    .then((resp) => {
      this.setState({ message: `Data Updated Successfully` });
      alert("sucess");
      // this.props.history.push("/Patient");
    })
    .catch((error) => {
      this.setState({ message: `Error Occured ${error}` });
    });
  };

  render() {
    return (
      <>
      <div align='right'>
       <Navbar style={{background:'black',width:1089 ,height:60}}>
       </Navbar>
      <div class="card" style={{width:1089} } >
        <form style={{width:1089}}>
        <div >
        <h3 align='center'>Register New Doctor </h3> 
               <div className='form-group row'>
               <label style={{fontFamily:"-moz-initial" ,fontSize:20}} class="col-sm-2 col-form-label">Doctor Id</label>
               <input 
               type='text'class="col-sm-9" 
               placeholder=' Doctor_Id'  
               name=' Doctor_Id'
               defaultValue={this.state.Doctor_Id}
               onChange={this.handleAllChanges.bind(this)}
               
               />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Doctor Name</label>
               <input type='text' class="col-sm-9" 
               placeholder='Doctor_Name' 
               name='Doctor_Name'
               onChange={this.handleAllChanges.bind(this)}
               defaultValue={this.state.Doctor_Name}
               />
               </div><br/>
               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Doctor Address</label>
               <input type='text' 
               class="col-sm-9" 
               placeholder='Doctor_Address' 
               name='Doctor_Address'
               onChange={this.handleAllChanges.bind(this)}
               value={this.props.Doctor_Address}
               />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Doctor City</label>
               <input type='text' 
               class="col-sm-9" 
               placeholder='Doctor_City' 
               name='Doctor_City'
               onChange={this.handleAllChanges.bind(this)}
               value={this.props.Doctor_City}
               />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Doctor Age</label>
               <input type='text'
                class="col-sm-9"
                 placeholder=' Doctor_Age' 
                 name=' Doctor_Age'
                 onChange={this.handleAllChanges.bind(this)}
                 value={this.props.Doctor_Age}
                 />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Patient Gender</label>
               <select class="col-sm-9"
               name='Patient_Gender'
                onChange={this.handleAllChanges.bind(this)}
                value={this.props.Patient_Gender}
                >
                 <option value="Male">Male</option>
                 <option value="Female">Female</option>
               </select>
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Date of Birth</label>
               <input type='date'
                class="col-sm-9"
                 placeholder='Date of Birth'
                 name='Date_of_Birth'
                  onChange={this.handleAllChanges.bind(this)}
                  value={this.props.Date_of_Birth}
                  />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Patient Disease</label>
               <input type='text'
                class="col-sm-9"
                 placeholder='Patient Disease'
                 name='Patient_Disease'
                  onChange={this.handleAllChanges.bind(this)}
                  value={this.props.Patient_Disease}
                  />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Patient Mobile No</label>
               <input type='text'
                class="col-sm-9"
                placeholder='Mobile No'
                name='Patient_Mobile_No'
                onChange={this.handleAllChanges.bind(this)}
                  value={this.props.Patient_Mobile_No}
                 ></input>
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Patient Type</label>
               <select class="col-sm-9"
               name='Patient_Type'
                 onChange={this.handleAllChanges.bind(this)}
                  value={this.props.Patient_Type}
               >
                 <option value="In Ward">IN WARD</option>
                 <option value="Out ward">OUT WARD</option>
               </select>
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Patient Ward ID</label>
               <input type='text'
                class="col-sm-9"
                 placeholder='Ward Id'
                 name='Patient_Ward_ID'
                 onChange={this.handleAllChanges.bind(this)}
                value={this.props.Patient_Ward_ID}
                 />
               </div><br/>

               <div align='center'>
               <input type="button" value="Submit"  className="btn btn-primary" onClick={this.save.bind(this)} /> <br/>
               </div>
</div>
              
          </form>
      </div>
      </div>
      </>

    );
  }
}

export default Adddoctor;
