import axios from 'axios';
class Medicineaxios {
//Gte doctor info
getmedicine(token){
        let response = axios.get('http://localhost:9082/api/medicineDetails',{
            headers:{
                'AUTHORIZATION': `Bearer ${token}`
            }
        });
        return response;
}

//post doctor info

postdoctorinfo(Doctor){
    let response =axios.post (`http://localhost:9082/api/doctorDetails`,Doctor);
    return response;
}
//delete doctorinfo


deletemedicine(medicineid)
{
    let response =axios.delete(`http://localhost:9082/api/medicineDetails/${medicineid}`)
    return response;
}

}
export default Medicineaxios;
