import React, { Component } from "react";
// import { SecureCallService } from "../../../HTTPandSERVICE/services/secureservice";

class CreatePatientComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //   Patient_id: "",
      Patient_Name: "",
      Patient_Email: "",
      Patient_Age: 0,
      Patient_Gender: "",
      Patient_Address: "",
      Patient_Phoneno: "",
      Patient_Addhaar: "",
      Patient_Disease: "",
      Patient_Ward_type: "",
      Patient_Room_no: 0,
      //   DeptNo: 0,
      //   Patient_Name: "",
      //   Patient_Email: "",
      //   Patient_Age: 0,
      departments: [],
      columnHeaders: [],
      message: "",
    };
    this.serv = new SecureCallService();
  }

  

  handleAllChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
  };

  clear = () => {
    // this.setState({ Patient_id: 0 });
    this.setState({ Patient_Name: "" });
    this.setState({ Patient_Email: "" });
    this.setState({ Patient_Age: 0 });
    this.setState({ Patient_Gender: "" });
    this.setState({ Patient_Address: "" });
    this.setState({ Patient_Phoneno: "" });
    this.setState({ Patient_Addhaar: "" });
    this.setState({ Patient_Disease: "" });
    this.setState({ Patient_Ward_type: "" });
    this.setState({ Patient_Room_no: 0 });
  };

  save = () => {
    console.log(`this is Patient change in save- ${this.state.Patient_Name}`);
    console.log(`this is Patient change in save- ${this.state.Patient_Age}`);
    console.log(
      `this is Patient change in save- ${this.state.Patient_Room_no}`
    );

    let Patient = {
      Patient_Name: this.state.Patient_Name,
      Patient_Email: this.state.Patient_Email,
      Patient_Age: this.state.Patient_Age,
      Patient_Gender: this.state.Patient_Gender,
      Patient_Address: this.state.Patient_Address,
      Patient_Phoneno: this.state.Patient_Phoneno,
      Patient_Addhaar: this.state.Patient_Addhaar,
      Patient_Disease: this.state.Patient_Disease,
      Patient_Ward_type: this.state.Patient_Ward_type,
      Patient_Room_no: this.state.Patient_Room_no,
    };
    console.log(`this is Patient change- ${JSON.stringify(Patient)}`);
    console.log("Inside double Save!!");
    this.serv
      .AddPatientData(Patient)
      .then((resp) => {
        console.log("Inside triple Save!!");
        this.setState({ message: `Data Updated Successfully` });
        console.log("Inside triple Save!!");
        this.props.history.push("/Patient");
      })
      .catch((error) => {
        this.setState({ message: `Error Occured ${error}` });
      });
  };

  render() {
    return (
      <div className="container">
        <h4>Add New Patient</h4>
        <form>
          <div className="form-group">
            <label htmlFor="Patient_Name">Patient_Name</label>
            <input
              type="text"
              name="Patient_Name"
              className="form-control"
              value={this.state.Patient_Name}
              onChange={this.handleAllChanges.bind(this)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="Patient_Email">Patient_Email</label>
            <input
              type="email"
              name="Patient_Email"
              className="form-control"
              value={this.state.Patient_Email}
              onChange={this.handleAllChanges.bind(this)}
            />
            <div className="form-group">
              <label htmlFor="Patient_Age">Patient_Age</label>
              <input
                type="number"
                name="Patient_Age"
                className="form-control"
                value={this.state.Patient_Age}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Gender">Patient_Gender</label>
              <input
                type="text"
                name="Patient_Gender"
                className="form-control"
                value={this.state.Patient_Gender}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Address">Patient_Address</label>
              <input
                type="text"
                name="Patient_Address"
                className="form-control"
                value={this.state.Patient_Address}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Phoneno">Patient_Phoneno</label>
              <input
                type="number"
                name="Patient_Phoneno"
                className="form-control"
                value={this.state.Patient_Phoneno}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Addhaar">Patient_Addhaar</label>
              <input
                type="number"
                name="Patient_Addhaar"
                className="form-control"
                value={this.state.Patient_Addhaar}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Disease">Patient_Disease</label>
              <input
                type="text"
                name="Patient_Disease"
                className="form-control"
                value={this.state.Patient_Disease}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Ward_type">Patient_Ward_type</label>
              <input
                type="text"
                name="Patient_Ward_type"
                className="form-control"
                value={this.state.Patient_Ward_type}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="Patient_Room_no">Patient_Room_no</label>
              <input
                type="number"
                name="Patient_Room_no"
                className="form-control"
                value={this.state.Patient_Room_no}
                onChange={this.handleAllChanges.bind(this)}
              />
            </div>
          </div>
          <hr />
          <div className="btn-group">
            <input
              type="button"
              value="Clear"
              className="btn btn-primary"
              onClick={this.clear.bind(this)}
            />
            <input
              type="button"
              value="Save"
              className="btn btn-success"
              onClick={this.save.bind(this)}
            />
          </div>
        </form>
        <hr />
        <div className="container">
          <strong>{this.state.message}</strong>
        </div>
      </div>
    );
  }
}

export default CreatePatientComponent;































