import React, { Component } from "react";
// import Roomwards from "./roomwardaxios";
import { Navbar } from "react-bootstrap";
class Adduserr extends Component {
  constructor(props) {
    super(props);
    this.state = {
        User_Id: 0,
        User_name:"",
        User_Type:"",
        Password: "",
      departments: [],
      columnHeaders: [],
      message: "",
    }
    // this.serv = new Roomwards();
  }
handleAllChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
    console.log(evt.target.value,evt.target.name);
  };
submit = () => {
  var Roomward = {
    idwr:this.state.User_Id,
    wardtype1: this.state.User_name,
    roomtype1: this.state.User_Type,
    roomfeesperday: this.state.Fees_per_day,
  };
  console.log(Roomward);
  this.serv.postroomward(Roomward)
    .then((resp) => {
        console.log(resp)
      this.setState({ message: `Data Updated Successfully` });
      alert("sucess");
      this.props.history.push("/homepage");
    })
    .catch((error) => {
      this.setState({ message: `Error Occured ${error}` });
    });
  };

  render() {
    return (
      <>
      <div align='right'>
       <Navbar style={{background:'black',width:1089 ,height:60}}>
       </Navbar>
      <div class="card" style={{width:1089} } >
        <form style={{width:1089}}>
        <div >
        <h3 align='center'>Register New User  </h3> 
               <div className='form-group row'>
               <label style={{fontFamily:"-moz-initial" ,fontSize:20}} class="col-sm-2 col-form-label">User Id</label>
               <input 
               type='text'class="col-sm-9" 
               placeholder='User_Id'  
               name='User_Id'
               defaultValue={this.state.User_Id}
               onChange={this.handleAllChanges.bind(this)}
               
               />
               </div><br/>

               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>User name</label>
               <input type='text' class="col-sm-9" 
               placeholder='User_name' 
               name='User_name'
               defaultValue={this.state.User_name}
               onChange={this.handleAllChanges.bind(this)}
               
               />
               </div><br/>
               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>User_Type</label>
               <input type='select' 
               class="col-sm-9" 
               placeholder='User_Type' 
               name='User_Type'
               onChange={this.handleAllChanges.bind(this)}
               value={this.props.User_Type}
               />
               </div><br/>
               <div className='form-group row'>
               <label class="col-sm-2 col-form-label" style={{fontFamily:"-moz-initial" ,fontSize:20}}>Password</label>
               <input type='text'
                class="col-sm-9"
                 placeholder='Password' 
                 name='Password'
                 onChange={this.handleAllChanges.bind(this)}
                 defaultValue={this.props.Password}
                 />
               </div><br/>
               <div align='center'>
               <input type="button" value="Submit"  className="btn btn-primary" onClick={this.submit.bind(this)}/><br/>
               </div>
              </div>  
          </form>
      </div>
      </div>
      </>

    );
  }
}
export default Adduserr;
