import React ,{ Component } from "react";
import {Route,Redirect,Link,Switch,NavLink} from "react-router-dom";
import  SecureCallService from "./patientaxios"
import axios from 'axios';
import { Navbar } from "react-bootstrap";
class Patient extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: "",
            departments: [],
            columnHeaders:[] 
          } ; 
          this.serv = new SecureCallService();
    }
handleupdate(c){
    // let ptid = parseInt(c.target.value)
    // let token = sessionStorage.getItem("token");
    // let response = axios.get(`http://localhost:9082/api/patientDetails/${ptid}`,{
    //         headers:{
    //             'AUTHORIZATION': `Bearer ${token}`
    //         }
    //     });
    //     console.log("hellllooooooo")
    //     response.then((resp) => {
    //       console.log(resp.data);
    //       console.log(resp.data.message);
    //                 let p = resp.data.message;
    //                 console.log(p);
    //                 this.setState({ message: `Data Received Successfully` });
    //             })
    //             .catch((error) => {
    //                 this.setState({ message: `Error Occured ${error.message}` });
    //             });

        let ptid = parseInt(c.target.value)
        let token = sessionStorage.getItem("token");
        if (token === undefined) {
          this.setState({ message: `Please send the Auth Header` });
        } else {
          console.log(`pt iddddd ${ptid}`)
          this.serv.getpatientbyid(ptid,token)
            .then((resp) => {
              this.setState({ departments: resp.data.message });
              this.setState({ message: `Data Received Successfully` });
            })
            .catch((error) => {
              this.setState({ message: `Error Occured ${error.message}` });
            });
        }
      }
handledelete(c){
        // let ptid = JSON.parse(c.target.value)
        // console.log(ptid)
        // let response = axios.delete(`http://localhost:9082/api/patientDetails/${ptid}`);
        // return response;
        // this.getValues();
    }
getValues() {
        let token = sessionStorage.getItem("token");
        if (token === undefined) {
          this.setState({ message: `Please send the Auth Header` });
        } else {
          this.serv.getpatient(token)
            .then((resp) => {
              this.setState({ departments: resp.data.message });
              this.setState({ message: `Data Received Successfully` });
              this.setState({columnHeaders:Object.keys(this.state.departments[0])})
            })
            .catch((error) => {
              this.setState({ message: `Error Occured ${error.message}` });
            });
        }
      }
    render() { 
        return ( 
          <>
        
         <div align="right">
           <Navbar style={{background:'black',width:1100}} >
           
          <input type="button" value="Patient Deatils" onClick={this.getValues.bind(this)}  /> 
          <div type="button" style={{height:30}}  > <ul> <NavLink link to='/patient/Addpatient' ClassName='active'>Add Patient</NavLink></ul></div>
  
          </Navbar>
          </div>
    
          <center>
            <div>
            <div align="right">
        <table style={{width:40}}  className="table table-bordered table table-hover">
          <thead>
            <tr>
              {this.state.columnHeaders.map((head, idx) => (
                <th key={idx}>{head}</th> 
              ))}
              <td>
                <th>Edit</th>
              </td>
              <td>
                <th>Delete</th>
              </td> 
            </tr>
          </thead>
          <tbody>
            {this.state.departments.map((doc, idx) => (
              <tr key={idx}>
                {this.state.columnHeaders.map((head, i) => (
                  <td key={i}>{doc[head]}</td>
                ))}
                <td>
                   <button className="btn btn-dark" onClick={this.handleupdate.bind(this)} value={doc.ptid}>Update</button>
                </td>
                <td>
                    <button className="btn btn-dark" onClick={this.handledelete.bind(this)} value={doc.ptid}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
            </div>
            </div>
            </center>
            </>
         );
    }
}
export default Patient;
