import React, { Component } from "react";
import Loginaxios from "./loginaxios";
class HttpCallComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      UserId: 0,
      RegUserName: "",
      RegPAssword: "",
      AuthUserName: "",
      AuthPAssword: "",
      message: "",
      departments: [],
      isRegUserNameValid:true,
      isRegPAsswordValid:true,
      isFormValid:true
    };
    this.serv = new Loginaxios();
  }

  clearReg = () => {
    this.setState({ UserId: 0 });
    this.setState({ RegUserName: "" });
    this.setState({ RegPAssword: "" });
  };
 
  register() {
    let user = {
      UserId: this.state.UserId,
      UserName: this.state.RegUserName,
      PAssword: this.state.RegPAssword,
};
    this.serv
      .registerUser(user)
      .then((resp) => {
        this.setState({ message: resp.data.message });
      })
      .catch((error) => {
        this.setState({ message: `Error Occured ${error.message}` });
      });
  }

  auth() {
    let user = {
      UserName: this.state.AuthUserName,
      PAssword: this.state.AuthPAssword,
    };
    this.serv
      .authUser(user)
      .then((resp) => {
        this.setState({ message: resp.data.message });
        sessionStorage.setItem("token", resp.data.token);
        alert('Login sucessfully')
        this.props.history.push('/homepage');
      })
      .catch((error) => {
        this.setState({ message: `Error Occured Enter a valid Id and  password` });
      });
  }
  handleInPutChanges = (evt) => {
    this.setState({ [evt.target.name]: evt.target.value });
    this.validateInputs(evt.target.name,evt.target.value);
  };

  // validation logic
validateInputs=(name,value)=>{
    if(name === 'RegUserName'){
        if(value.length === 0) {
            this.setState({isRegUserNameValid:false});
            this.setState({isFormValid:false});
            alert('Enter valid email')
        }else {
            this.setState({isRegUserNameValid:true});
            this.setState({isFormValid:true});
        }
    }
    if(name === 'RegPAssword'){
        if(value.length === 0) {
            this.setState({isRegPAsswordValid:false});
            this.setState({isFormValid:false});
        }else {
            this.setState({isRegPAsswordValid:true});
            this.setState({isFormValid:true});
        }
    }

  }

  render() {
    return (
      <div className="container-lg">
        <h3>Register User </h3>
          <form className='calculator card' style={{width:500} }>
          UserId
          <input type="text" name="UserId" onChange={this.handleInPutChanges.bind(this)} className="form-control"/>
          User Name
          <input type="text" name="RegUserName" value={this.state.RegUserName} onChange={this.handleInPutChanges.bind(this)} className="form-control" />
          Password
          <input type="password" className="form-control" name="RegPAssword" value={this.state.RegPAssword} onChange={this.handleInPutChanges.bind(this)} />
          <div className="alert alert-danger" hidden={this.state.isRegPAsswordValid}> The Password is Required </div>
          <input type="button" value="Clear" onClick={this.clearReg.bind(this)} className="btn btn-primary"/><br/>
          <input type="button" value="Register" onClick={this.register.bind(this)} disabled={!this.state.isFormValid} className="btn btn-light"/>
          </form >
            <form className='calculator card' style={{width:500}}>
            <h1 style={{fontFamily:"-moz-initial" ,fontSize:30,background:"blue"}}>Login</h1>
           <label style={{fontFamily:"-moz-initial" ,fontSize:30}}> User Name</label>
            <input type="text" name="AuthUserName" className="form-control" value={this.state.AuthUserName} onChange={this.handleInPutChanges.bind(this)} />
            <label style={{fontFamily:"-moz-initial" ,fontSize:30}}>  Password   </label>
            <input type="password" name="AuthPAssword" className="form-control" value={this.state.AuthPAssword} onChange={this.handleInPutChanges.bind(this)} /><br/>
            <input type="button" value="Login" onClick={this.auth.bind(this)} className="btn btn-primary" /> <br/>
            <div className="container"> <strong>{this.state.message}</strong></div><br/>
            </form>
            
          
</div>
    );
  }
}

export default HttpCallComponent;
