import axios from "axios";

class Wardboyaxioss{

//Get nurse Data

getwardboy(token){
let response=axios.get(`http://localhost:9082/api/wardboyDetails`,{
    
    headers:{
        'AUTHORIZATION': `Bearer ${token}`
    }
});
console.log(response);
return response;

}
//Delete nurse info
deletenurse(nid){
    console.log(nid)
    let response=axios.delete(`http://localhost:9082/api/nurseDetails/${nid}`)
    return response;
}
//Add new nurse 
postnurseinfo(Nurse){
    let response=axios.post(`http://localhost:9082/api/nurseDetails`,Nurse)
    return response;
    
}
}

export default Wardboyaxioss;
