import axios from 'axios';
export class SecureCallService {
registerUser(user){
        let response =  axios.post('http://localhost:9082/api/app/register', user, {
            headers:{
                'Content-Type': 'application/json'
            }
        });
        return response;
    }
authUser(user){
        let response =  axios.post('http://localhost:9082/api/app/auth', user, {
            headers:{
                'Content-Type': 'application/json'
            }
        });
        return response;
    }
getpatient(token){
        let response = axios.get('http://localhost:9082/api/patientDetails/',{
            headers:{
                'AUTHORIZATION': `Bearer ${token}`
            }
        });
        return response;
    }
deletepatient(id){
        // console.log(`this is pt id ${ptid}`)
        let response = axios.delete(`http://localhost:9082/api/patientDetails/:${id}`
        );
        return response;
    } 
}
